mapper.select:
SELECT PP.PROCESS_PERFOM_NO, FC.FAULTY_CD_CODE, FC.FAULTY_NAME, FPD.FLTY_PRCS_NO, FPD.FLTY_PRCS_DATE, NVL(FPD.FLTY_PRCS_REMK, ' ') AS FLTY_PRCS_REMK, PP.FLTY_VOL, P.PROC_CD_NAME, P.MCHN_NAME, IDE.FIN_PRD_CD_CODE, FPC.FIN_PRD_CD_NAME, EMP.EMP_NAME FROM BF_PROD_FLTY_PRCS_DETAILS FPD JOIN BF_CMN_FAULTY_CODE FC ON FPD.FAULTY_CD_CODE = FC.FAULTY_CD_CODE JOIN BF_PROD_PROCESS_PERFORMANCE PP ON FPD.PROCESS_PERFOM_NO = PP.PROCESS_PERFOM_NO JOIN BF_PROD_PROCESS P ON PP.PROCESS_NO = P.PROCESS_NO JOIN BF_PROD_INST_DETAIL IDE ON P.INST_PROD_NO = IDE.INST_PROD_NO JOIN BF_CMN_FINISH_PRODUCT_CODE FPC ON IDE.FIN_PRD_CD_CODE = FPC.FIN_PRD_CD_CODE JOIN BF_CMN_EMP EMP ON FPD.EMP_ID = EMP.EMP_ID WHERE 1=1
AND FPD.FLTY_PRCS_DATE BETWEEN #{fltyPrcsSdate} AND #{fltyPrcsEdate}
ORDER BY FLTY_PRCS_DATE

SELECT PP.PROCESS_PERFOM_NO, FC.FAULTY_CD_CODE, FC.FAULTY_NAME, FPD.FLTY_PRCS_DATE, NVL(FPD.FLTY_PRCS_REMK, ' ') AS FLTY_PRCS_REMK, PP.FLTY_VOL, P.PROC_CD_NAME, P.MCHN_NAME, IDE.FIN_PRD_CD_CODE, FPC.FIN_PRD_CD_NAME, EMP.EMP_NAME FROM BF_PROD_FLTY_PRCS_DETAILS FPD JOIN BF_CMN_FAULTY_CODE FC ON FPD.FAULTY_CD_CODE = FC.FAULTY_CD_CODE JOIN BF_PROD_PROCESS_PERFORMANCE PP ON FPD.PROCESS_PERFOM_NO = PP.PROCESS_PERFOM_NO JOIN BF_PROD_PROCESS P ON PP.PROCESS_NO = P.PROCESS_NO JOIN BF_PROD_INST_DETAIL IDE ON P.INST_PROD_NO = IDE.INST_PROD_NO JOIN BF_CMN_FINISH_PRODUCT_CODE FPC ON IDE.FIN_PRD_CD_CODE = FPC.FIN_PRD_CD_CODE JOIN BF_CMN_EMP EMP ON FPD.EMP_ID = EMP.EMP_ID WHERE 1=1
AND FPC.FIN_PRD_CD_NAME = #{finPrdCdName}
AND FPD.FLTY_PRCS_DATE BETWEEN #{fltyPrcsSdate} AND #{fltyPrcsEdate}
ORDER BY FLTY_PRCS_DATE
