mapper.select:
SELECT PH.PLAN_HD_CODE, PH.PLAN_HD_NAME, PH.PLAN_HD_DATE, PH.EMP_ID, P.FIN_PRD_CD_CODE, F.FIN_PRD_CD_NAME, P.PLAN_PROD_VOL, P.PLAN_SDATE, P.PLAN_EDATE, P.PLAN_REMK, OH.SLS_ORD_HD_NO AS SLS_ORD_HD_NO, OH.VEND_CD_CODE, V.VEND_CD_NM, O.SLS_ORD_DTL_VOL, O.SLS_ORD_DTL_DLV_DATE FROM BF_PROD_PLAN P JOIN BF_PROD_PLAN_HD PH ON P.PLAN_HD_CODE = PH.PLAN_HD_CODE JOIN BF_CMN_FINISH_PRODUCT_CODE F ON P.FIN_PRD_CD_CODE = F.FIN_PRD_CD_CODE LEFT OUTER JOIN BF_SALES_ORD_HD OH ON OH.SLS_ORD_HD_NO = PH.SLS_ORD_HD_NO LEFT OUTER JOIN BF_CMN_VENDOR_CODE V ON OH.VEND_CD_CODE = V.VEND_CD_CODE LEFT OUTER JOIN BF_SALES_ORD_DTL O ON O.SLS_ORD_HD_NO = OH.SLS_ORD_HD_NO AND P.FIN_PRD_CD_CODE = O.FIN_PRD_CD_CODE WHERE 1=1
AND PH.PLAN_HD_CODE = #{data}
AND OH.VEND_CD_CODE = #{data}
ORDER BY PLAN_HD_DATE DESC

SELECT DISTINCT PH.PLAN_HD_CODE, PH.PLAN_HD_NAME, NVL(PH.SLS_ORD_HD_NO,' ') AS SLS_ORD_HD_NO, PH.EMP_ID, PH.PLAN_HD_DATE, NVL(PH.PLAN_HD_REMK,' ') AS PLAN_HD_REMK FROM BF_PROD_PLAN P JOIN BF_PROD_PLAN_HD PH ON P.PLAN_HD_CODE = PH.PLAN_HD_CODE JOIN BF_CMN_FINISH_PRODUCT_CODE F ON P.FIN_PRD_CD_CODE = F.FIN_PRD_CD_CODE WHERE 1=1
AND PH.EMP_ID = #{empId}
ORDER BY PLAN_HD_CODE DESC
