mapper.select:
SELECT * FROM v_findprocperform



		 AND TO_CHAR(workdate, 'yyyy-MM-dd') >= #{workSdate} 



		 AND TO_CHAR(workdate, 'yyyy-MM-dd') <= #{workEdate} 



		AND proc_cd_name = #{procCdName}



		AND mchn_name = #{mchnName}



		AND workername = #{empId}



ORDER BY workdate DESC,processperfomno

SELECT instno, instprodno, workdate, finprdcdcode, finprdcdname, instprodindicavol, workscope
FROM v_procmanage
NOT workscope = '진행완료'
AND finPrdCdName LIKE #{finPrdCdName}
AND to_char(workDate,'yyyy-mm-dd') = #{workDate}
ORDER BY workdate DESC

mapper.update:

UPDATE 
	BF_PROD_PROCESS 

	total_prod_vol = #{totalProdVol},

	flty_vol = #{fltyVol}


WHERE 
	process_no = #{processNo}

UPDATE
	BF_EQP_MCHN
SET 
	mchn_stts = #{mchnStts}
WHERE
	mchn_code = #{mchnCode}

UPDATE
	BF_PROD_PROCESS
SET
	achie_rate = #{achieRate}	
WHERE
	process_no = #{processNo}

		UPDATE 
				BF_PROD_PROCESS 
		SET 
				in_dtl_vol = #{inDtlVol}
 		WHERE 
				inst_prod_no = #{instProdNo}
 		AND 
				process_order = #{processOrder}

UPDATE 
	BF_PROD_INST_DETAIL 
SET 
	work_scope = #{workScope} 
WHERE 
	inst_prod_no = #{instProdNo}
