mapper.update:
UPDATE BF_RSC_INSP
SET RSC_INSP_DATE = #{rscInspDate}, RSC_INSP_VOL = #{rscInspVol}, RSC_INFER_VOL = #{rscInferVol}, RSC_PASS_VOL = (#{rscInspVol} - #{rscInferVol}), RSC_INSP_REMK = #{rscInspRemk}, EMP_ID = #{empId}
WHERE RSC_INSP_CODE = #{rscInspCode}

mapper.select:
SELECT X.* FROM ( SELECT Y.*, ROWNUM AS RNUM FROM (SELECT INSP.RSC_INSP_CODE, INSP.RSC_INSP_DATE, INSP.RSC_ORDER_CODE, INSP.RSC_ORDER_DTL_NO, INSP.RSC_CD_CODE, INSP.RSC_INSP_VOL, INSP.RSC_INFER_VOL, INSP.RSC_PASS_VOL, INSP.RSC_INSP_ST, INSP.RSC_INSP_REMK, INSP.EMP_ID,
RSCCODE.RSC_CD_NAME, RSCCODE.RSC_CD_UNIT,
EMP.EMP_NAME,
DT.RSC_ORDER_VOL, DT.RSC_ORDER_ARV
FROM BF_RSC_INSP INSP LEFT JOIN BF_CMN_RSC_CODE RSCCODE ON INSP.RSC_CD_CODE = RSCCODE.RSC_CD_CODE LEFT JOIN BF_CMN_EMP EMP ON INSP.EMP_ID = EMP.EMP_ID LEFT JOIN BF_RSC_ORDER_DETAILS DT ON INSP.RSC_ORDER_DTL_NO = DT.RSC_ORDER_DTL_NO
ORDER BY 1 DESC ) Y
AND ROWNUM <= #{pageNum}*#{amount}
) X WHERE X.RNUM > (#{pageNum}-1)*#{amount}
