mapper.select:
SELECT X.* FROM ( SELECT Y.*, ROWNUM AS RNUM FROM ( SELECT RSCOUT.RSC_OUT_CODE, RSCOUT.RSC_OUT_DATE, RSCOUT.PROCESS_PERFOM_NO, RSCOUT.RSC_CD_CODE, RSCOUT.RSC_LOT_NO, RSCOUT.RSC_OUT_VOL, RSCOUT.RSC_OUT_CLS,
RSCCODE.RSC_CD_NAME
FROM BF_RSC_OUT RSCOUT LEFT JOIN BF_CMN_RSC_CODE RSCCODE ON RSCOUT.RSC_CD_CODE = RSCCODE.RSC_CD_CODE
WHERE RSCOUT.RSC_OUT_CLS = 1
ORDER BY 1 DESC) Y
AND ROWNUM <= #{pageNum}*#{amount}
) X WHERE X.RNUM > (#{pageNum}-1)*#{amount}

SELECT X.* FROM ( SELECT Y.*, ROWNUM AS RNUM FROM ( SELECT RSCOUT.RSC_OUT_CODE, RSCOUT.RSC_OUT_DATE, NVL(RSCOUT.VEND_CD_CODE,''), RSCOUT.RSC_CD_CODE, RSCOUT.RSC_LOT_NO, RSCOUT.RSC_OUT_VOL, RSCOUT.RSC_OUT_CLS, RSCOUT.RSC_OUT_RESN,
RSCCODE.RSC_CD_NAME,
VEND.VEND_CD_NM,
EMP.EMP_NAME
FROM BF_RSC_OUT RSCOUT LEFT JOIN BF_CMN_RSC_CODE RSCCODE ON RSCOUT.RSC_CD_CODE = RSCCODE.RSC_CD_CODE LEFT JOIN BF_CMN_VENDOR_CODE VEND ON RSCOUT.VEND_CD_CODE = VEND.VEND_CD_CODE LEFT JOIN BF_CMN_EMP EMP ON RSCOUT.EMP_ID = EMP.EMP_ID
WHERE RSCOUT.RSC_OUT_CLS = 2
ORDER BY 1 DESC ) Y
AND ROWNUM <= #{pageNum}*#{amount}
) X WHERE X.RNUM > (#{pageNum}-1)*#{amount}
