mapper.select:
SELECT DISTINCT H.SLS_ORD_HD_NO, H.SLS_ORD_HD_DATE, H.VEND_CD_CODE, V.VEND_CD_NM, H.EMP_ID, NVL(H.SLS_ORD_HD_REMK,' ') AS SLS_ORD_HD_REMK
,D.SLS_ORD_DTL_NO, D.FIN_PRD_CD_CODE, F.FIN_PRD_CD_NAME, D.SLS_ORD_DTL_DLV_DATE, D.SLS_ORD_DTL_VOL, D.SLS_ORD_DTL_PRG_CLS, D.SLS_ORD_DTL_NO, NVL(P.PLAN_PROD_VOL,0) AS PLAN_PROD_VOL
FROM BF_SALES_ORD_HD H JOIN BF_SALES_ORD_DTL D ON H.SLS_ORD_HD_NO = D.SLS_ORD_HD_NO JOIN BF_CMN_VENDOR_CODE V ON V.VEND_CD_CODE = H.VEND_CD_CODE JOIN BF_CMN_FINISH_PRODUCT_CODE F ON F.FIN_PRD_CD_CODE = D.FIN_PRD_CD_CODE LEFT OUTER JOIN (SELECT H.PLAN_HD_CODE, P.FIN_PRD_CD_CODE, SUM(P.PLAN_PROD_VOL) AS PLAN_PROD_VOL, H.SLS_ORD_HD_NO FROM BF_PROD_PLAN P JOIN BF_PROD_PLAN_HD H ON P.PLAN_HD_CODE = H.PLAN_HD_CODE GROUP BY H.PLAN_HD_CODE, H.SLS_ORD_HD_NO, P.FIN_PRD_CD_CODE HAVING H.SLS_ORD_HD_NO IS NOT NULL) P ON (H.SLS_ORD_HD_NO = P.SLS_ORD_HD_NO AND D.FIN_PRD_CD_CODE = P.FIN_PRD_CD_CODE) WHERE ((D.SLS_ORD_dTL_PRG_CLS = 1 AND D.SLS_ORD_DTL_VOL > P.PLAN_PROD_VOL) OR D.SLS_ORD_DTL_PRG_CLS = 0)
AND H.SLS_ORD_HD_NO = #{ordType}
AND D.sls_ord_dtl_dlv_date BETWEEN #{ordSdate} AND #{ordEdate}
AND D.sls_ord_dtl_dlv_date = #{ordSdate}
AND D.sls_ord_dtl_dlv_date = #{ordEdate}
AND D.sls_ord_dtl_dlv_date > (sysdate - 10)
ORDER BY SLS_ORD_HD_DATE DESC
