mapper.select:
SELECT X.* FROM( SELECT Y.*, ROWNUM AS RNUM FROM ( SELECT ORDHD.SLS_ORD_HD_DATE, ORDHD.SLS_ORD_HD_NO, VEND.VEND_CD_NM, FNSPRD.FIN_PRD_CD_CODE, FNSPRD.FIN_PRD_CD_NAME, ORDDTL.SLS_ORD_DTL_DLV_DATE, ORDDTL.SLS_ORD_DTL_VOL, ORDDTL.SLS_ORD_DTL_OUT_VOL, EMP.EMP_NAME, NVL(ORDHD.SLS_ORD_HD_REMK, ' ') AS SLS_ORD_HD_REMK FROM BF_SALES_ORD_HD ORDHD JOIN BF_SALES_ORD_DTL ORDDTL ON ORDHD.SLS_ORD_HD_NO = ORDDTL.SLS_ORD_HD_NO JOIN BF_CMN_EMP EMP ON EMP.EMP_ID = ORDHD.EMP_ID JOIN BF_CMN_VENDOR_CODE VEND ON VEND.VEND_CD_CODE = ORDHD.VEND_CD_CODE JOIN BF_CMN_FINISH_PRODUCT_CODE FNSPRD ON FNSPRD.FIN_PRD_CD_CODE = ORDDTL.FIN_PRD_CD_CODE
ORDER BY ORDHD.SLS_ORD_HD_DATE DESC, ORDHD.SLS_ORD_HD_NO DESC ) Y
AND ROWNUM <= #{pageNum}*#{amount}
) X WHERE X.RNUM > (#{pageNum}-1)*#{amount}

SELECT DISTINCT ORDHD.SLS_ORD_HD_NO, ORDHD.SLS_ORD_HD_DATE, VEND.VEND_CD_CODE, VEND.VEND_CD_NM, EMP.EMP_NAME, NVL(ORDHD.SLS_ORD_HD_REMK, ' ') AS SLS_ORD_HD_REMK FROM BF_SALES_ORD_HD ORDHD JOIN BF_CMN_VENDOR_CODE VEND ON ORDHD.VEND_CD_CODE = VEND.VEND_CD_CODE JOIN BF_CMN_EMP EMP ON ORDHD.EMP_ID = EMP.EMP_ID JOIN BF_SALES_ORD_DTL ORDDTL ON ORDDTL.SLS_ORD_HD_NO = ORDHD.SLS_ORD_HD_NO WHERE ORDDTL.SLS_ORD_DTL_PRG_CLS = 0
AND TRUNC(ORDHD.SLS_ORD_HD_DATE) BETWEEN #{ordSdate} AND #{ordEdate}
ORDER BY SLS_ORD_HD_DATE DESC, SLS_ORD_HD_NO DESC

mapper.delete:
DELETE FROM BF_SALES_ORD_DTL
WHERE SLS_ORD_DTL_NO IN

	#{priKey}

mapper.select:
SELECT DISTINCT OH.SLS_ORD_HD_NO, OH.SLS_ORD_HD_DATE, OH.VEND_CD_CODE, V.VEND_CD_NM, OH.EMP_ID, NVL(OH.SLS_ORD_HD_REMK,' ') AS SLS_ORD_HD_REMK FROM BF_SALES_ORD_HD OH JOIN BF_SALES_ORD_DTL O ON OH.SLS_ORD_HD_NO = O.SLS_ORD_HD_NO JOIN BF_CMN_VENDOR_CODE V ON V.VEND_CD_CODE = OH.VEND_CD_CODE LEFT OUTER JOIN (SELECT PH.SLS_ORD_HD_NO, P.FIN_PRD_CD_CODE, SUM(P.PLAN_PROD_VOL) AS PLAN_PROD_VOL FROM BF_PROD_PLAN P LEFT OUTER JOIN BF_PROD_PLAN_HD PH ON P.PLAN_HD_CODE = PH.PLAN_HD_CODE WHERE PLAN_PRE_VOL IS NOT NULL GROUP BY PH.SLS_ORD_HD_NO, P.FIN_PRD_CD_CODE) P ON P.SLS_ORD_HD_NO = OH.SLS_ORD_HD_NO AND P.FIN_PRD_CD_CODE = O.FIN_PRD_CD_CODE WHERE (O.SLS_ORD_DTL_VOL > P.PLAN_PROD_VOL OR PLAN_PROD_VOL IS NULL)
AND OH.SLS_ORD_HD_DATE BETWEEN TO_DATE(#{ordSdate}, 'YY/MM/DD') AND TO_DATE(#{ordEdate}, 'YY/MM/DD') + 0.9999
AND OH.SLS_ORD_HD_DATE = #{ordSdate}
AND OH.SLS_ORD_HD_DATE = #{ordEdate}
ORDER BY SLS_ORD_HD_DATE DESC, OH.SLS_ORD_HD_NO DESC

SELECT OH.SLS_ORD_HD_NO, O.FIN_PRD_CD_CODE, F.FIN_PRD_CD_NAME, O.SLS_ORD_DTL_DLV_DATE, O.SLS_ORD_DTL_VOL, NVL(P.PLAN_PROD_VOL,0) AS PLAN_PRE_VOL FROM BF_SALES_ORD_HD OH JOIN BF_SALES_ORD_DTL O ON OH.SLS_ORD_HD_NO = O.SLS_ORD_HD_NO JOIN BF_CMN_FINISH_PRODUCT_CODE F ON F.FIN_PRD_CD_CODE = O.FIN_PRD_CD_CODE LEFT OUTER JOIN (SELECT PH.SLS_ORD_HD_NO, P.FIN_PRD_CD_CODE, SUM(P.PLAN_PROD_VOL) AS PLAN_PROD_VOL FROM BF_PROD_PLAN P LEFT OUTER JOIN BF_PROD_PLAN_HD PH ON P.PLAN_HD_CODE = PH.PLAN_HD_CODE WHERE PLAN_PRE_VOL IS NOT NULL GROUP BY PH.SLS_ORD_HD_NO, P.FIN_PRD_CD_CODE) P ON P.SLS_ORD_HD_NO = OH.SLS_ORD_HD_NO AND P.FIN_PRD_CD_CODE = O.FIN_PRD_CD_CODE WHERE (O.SLS_ORD_DTL_VOL > P.PLAN_PROD_VOL OR P.PLAN_PROD_VOL IS NULL)
AND O.SLS_ORD_HD_NO = #{slsOrdHdNo}
ORDER BY SLS_ORD_HD_NO
