mapper.select:
SELECT OUTHD.SLS_OUT_HD_DATE, OUTHD.SLS_OUT_HD_NO, VEND.VEND_CD_NM, FNSPRD.FIN_PRD_CD_CODE, FNSPRD.FIN_PRD_CD_NAME, OUTHD.SLS_ORD_HD_NO, ORDDTL.SLS_ORD_DTL_VOL, OUTDTL.SLS_OUT_DTL_PRVS_VOL, OUTDTL.SLS_OUT_DTL_VOL, OUTDTL.FNS_PRD_STK_LOT_NO, FNSPRD.FIN_PRD_CD_PRICE, OUTDTL.SLS_OUT_DTL_PRICE, EMP.EMP_NAME, NVL(OUTHD.SLS_OUT_HD_REMK, ' ') AS SLS_OUT_HD_REMK FROM BF_SALES_OUT_HD OUTHD JOIN BF_SALES_OUT_DTL OUTDTL ON OUTHD.SLS_OUT_HD_NO = OUTDTL.SLS_OUT_HD_NO JOIN BF_SALES_ORD_HD ORDHD ON ORDHD.SLS_ORD_HD_NO = OUTHD.SLS_ORD_HD_NO JOIN BF_SALES_ORD_DTL ORDDTL ON ORDHD.SLS_ORD_HD_NO = ORDDTL.SLS_ORD_HD_NO JOIN BF_CMN_EMP EMP ON EMP.EMP_ID = OUTHD.EMP_ID JOIN BF_CMN_VENDOR_CODE VEND ON VEND.VEND_CD_CODE = OUTHD.VEND_CD_CODE JOIN BF_CMN_FINISH_PRODUCT_CODE FNSPRD ON FNSPRD.FIN_PRD_CD_CODE = OUTDTL.FIN_PRD_CD_CODE JOIN BF_SALES_FINISH_PRODUCT_STOK FNSPRDSK ON FNSPRDSK.FNS_PRD_STK_LOT_NO = OUTDTL.FNS_PRD_STK_LOT_NO
WHERE ORDDTL.FIN_PRD_CD_CODE = OUTDTL.FIN_PRD_CD_CODE
AND TRUNC(OUTHD.SLS_OUT_HD_DATE) BETWEEN #{outSdate} AND #{outEdate}
AND VEND.VEND_CD_NM = #{vendorName}
ORDER BY SLS_OUT_HD_DATE DESC, SLS_OUT_HD_NO DESC

SELECT DISTINCT OUTHD.SLS_OUT_HD_NO, OUTHD.SLS_OUT_HD_DATE, OUTHD.SLS_ORD_HD_NO, VEND.VEND_CD_CODE, VEND.VEND_CD_NM, EMP.EMP_ID, EMP.EMP_NAME, NVL(OUTHD.SLS_OUT_HD_REMK, ' ') AS SLS_OUT_HD_REMK FROM BF_SALES_OUT_HD OUTHD JOIN BF_CMN_VENDOR_CODE VEND ON VEND.VEND_CD_CODE = OUTHD.VEND_CD_CODE JOIN BF_CMN_EMP EMP ON OUTHD.EMP_ID = EMP.EMP_ID
WHERE TRUNC(SLS_OUT_HD_DATE) BETWEEN #{outSdate} AND #{outEdate}
ORDER BY SLS_OUT_HD_DATE DESC, SLS_OUT_HD_NO DESC

mapper.update:
UPDATE BF_SALES_OUT_DTL
SET SLS_OUT_DTL_VOL = #{slsOutDtlVol}
WHERE SLS_OUT_DTL_NO = #{slsOutDtlNo}

mapper.delete:
DELETE FROM BF_SALES_OUT_DTL
WHERE SLS_OUT_DTL_NO IN

	#{priKey}

mapper.select:
SELECT COUNT(SLS_ORD_HD_NO) AS CHECK_ORD_COUNT
FROM BF_SALES_OUT_HD
WHERE SLS_ORD_HD_NO = #{slsOrdHdNo}
