mapper.select:
SELECT INDTL.SLS_IN_DTL_DATE, NVL(INDTL.SLS_IN_DTL_REMK, ' ') AS SLS_IN_DTL_REMK,
PRDCD.FIN_PRD_CD_CODE, PRDCD.FIN_PRD_CD_NAME,
STOCK.FNS_PRD_STK_VOL, STOCK.FNS_PRD_STK_LOT_NO
FROM BF_SALES_FINISH_PRODUCT_STOK STOCK JOIN BF_CMN_FINISH_PRODUCT_CODE PRDCD ON STOCK.FIN_PRD_CD_CODE = PRDCD.FIN_PRD_CD_CODE JOIN BF_SALES_IN_DTL INDTL ON STOCK.SLS_IN_DTL_NO = INDTL.SLS_IN_DTL_NO
WHERE FNS_PRD_STK_VOL > 0
AND FIN_PRD_CD_NAME = #{prdName}
AND FNS_PRD_STK_LOT_NO = #{lotNo}
AND FIN_PRD_CD_NAME = #{prdName} AND FNS_PRD_STK_LOT_NO = #{lotNo}
ORDER BY SLS_IN_DTL_DATE DESC

SELECT OUTD.SLS_OUT_DTL_NO, INDTL.SLS_IN_DTL_DATE, PRD.FIN_PRD_CD_CODE, PRD.FIN_PRD_CD_NAME, STOK.FNS_PRD_STK_LOT_NO, STOK.FNS_PRD_STK_VOL, OUTD.SLS_OUT_DTL_VOL
FROM BF_SALES_OUT_DTL OUTD JOIN BF_SALES_FINISH_PRODUCT_STOK STOK ON STOK.FNS_PRD_STK_LOT_NO = OUTD.FNS_PRD_STK_LOT_NO JOIN BF_SALES_IN_DTL INDTL ON INDTL.SLS_IN_DTL_NO = STOK.SLS_IN_DTL_NO JOIN BF_CMN_FINISH_PRODUCT_CODE PRD ON PRD.FIN_PRD_CD_CODE = OUTD.FIN_PRD_CD_CODE
WHERE OUTD.SLS_OUT_HD_NO = #{slsOutHdNo} AND STOK.FIN_PRD_CD_CODE = #{finPrdCdCode}
